import React, { useState }  from 'react';
import './App.css';
import Header from './components/common/header/Header';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import About from './components/about/About';
import CourseHome from './components/allcourses/CourseHome';
import Team from './components/team/Team';
import Pricing from './components/pricing/Pricing';
import Blog from './components/blog/Blog';
import Contact from './components/contact/Contact';
import Footer from './components/common/footer/Footer';
import Home from './components/home/Home';
import config from "./chatbot/config";
import MessageParser from "./chatbot/messageParsers";
import ActionProvider from "./chatbot/actionProvider";
import { useSelector } from "react-redux";
import Chatbot from "react-chatbot-kit";
import 'react-chatbot-kit/build/main.css';
import Final from "./chatbot/Final";
import { Link } from 'react-router-dom';
import ChatPage from './components/chatPage/ChatPage';




function App() {
  const page3 = useSelector((state) => state.counter.page3)

  const [enroll, setEnroll] = useState(false);

  function handleChange(){
    setEnroll(true)
  }
  const chatbotRef = useRef(null);

  useEffect(() => {
    const handleLoad = () => {
      if(chatbotRef.current) {
        chatbotRef.current.scrollIntoView({ behavior: 'smooth', block: 'center' });
      }
    };

    window.addEventListener('load', handleLoad);
    // Déclencher également le scroll lorsque toutes les images et ressources de la page sont chargées
    return () => window.removeEventListener('load', handleLoad);
  }, []);

  return (
    <>
      <Router>
        <Header />
        <Routes>
          <Route path='/' element={<Home />} />
          <Route path='/about' element={<About />} />
          <Route path='/courses' element={<CourseHome />} />
          <Route path='/team' element={<Team />} />
          <Route path='/pricing' element={<Pricing />} />
          <Route path='/journal' element={<Blog />} />
          <Route path='/contact' element={<Contact />} />
          <Route path='/ChatPage' element={<ChatPage />} />
        </Routes>
        {enroll ? 

    (page3 ? <Final /> : <div className="App">
    <Chatbot
      config={config}
      messageParser={MessageParser}
      actionProvider={ActionProvider}
    />
  </div>)
    : 

    <div  >






    </div>}

    <br/>  <br/>  <br/>  <br/> <Footer />
      </Router>
    </>
  );
}

export default App; // Assurez-vous que cette ligne existe pour exporter par défaut le composant App
