import React from "react";
import "./Hero.css"; // Assurez-vous d'importer le fichier CSS si nécessaire

const slides = [
  {
    title: "Machu Picchu",
    subtitle: "Peru",
    description: "Adventure is never far away",
    image: require("./logo191.png") // Chemin relatif vers l'image
  },
  {
    title: "Chamonix",
    subtitle: "France",
    description: "Let your dreams come true",
    image: require("./logo192.png") // Chemin relatif vers l'image
  },
  {
    title: "Mimisa Rocks",
    subtitle: "Australia",
    description: "A piece of heaven",
    image:
      "https://images.unsplash.com/photo-1566522650166-bd8b3e3a2b4b?ixlib=rb-1.2.1&q=80&fm=jpg&crop=entropy&cs=tinysrgb&w=800&fit=max&ixid=eyJhcHBfaWQiOjE0NTg5fQ"
  },
  // Ajoutez d'autres objets de diapositive avec des images ici si nécessaire
];

function Slide({ slide, offset }) {
  return (
    <div
      className="slide"
      data-active={offset === 0 ? true : null}
      style={{
        "--offset": offset,
        "--dir": offset === 0 ? 0 : offset > 0 ? 1 : -1,
        "--background-image": `url(${slide.image})`
      }}
    >
      <div className="slideContent">
        <div className="slideContentInner">
          <h2 className="slideTitle">{slide.title}</h2>
          <h3 className="slideSubtitle">{slide.subtitle}</h3>
          <p className="slideDescription">{slide.description}</p>
        </div>
      </div>
    </div>
  );
}

function Hero() {
  const [slideIndex, setSlideIndex] = React.useState(0);

  const handleNextSlide = () => {
    setSlideIndex((prevIndex) => (prevIndex + 1) % slides.length);
  };

  const handlePrevSlide = () => {
    setSlideIndex((prevIndex) => (prevIndex === 0 ? slides.length - 1 : prevIndex - 1));
  };

  return (
    <div className="slides">
      <button onClick={handlePrevSlide}>‹</button>
      {slides.map((slide, i) => (
        <Slide slide={slide} offset={slides.length + (slideIndex - i)} key={i} />
      ))}
      <button onClick={handleNextSlide}>›</button>
    </div>
  );
}

export default Hero;
