import React, { useState } from 'react';
import './App.css';
import Header from './components/common/header/Header';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import About from './components/about/About';
import CourseHome from './components/allcourses/CourseHome';
import Team from './components/team/Team';
import Pricing from './components/pricing/Pricing';
import Blog from './components/blog/Blog';
import Contact from './components/contact/Contact';
import Footer from './components/common/footer/Footer'; // Assurez-vous d'importer Footer depuis l'emplacement correct
import Home from './components/home/Home';
import config from "./chatbot/config";
import MessageParser from "./chatbot/messageParsers";
import ActionProvider from "./chatbot/actionProvider";
import { useSelector } from "react-redux";
import Chatbot from "react-chatbot-kit";
import 'react-chatbot-kit/build/main.css';
import Final from "./chatbot/Final";


function App() {
  const page3 = useSelector((state) => state.counter.page3)
  const [enroll, setEnroll] = useState(false);

  function handleChange() {
    setEnroll(true)
  }

  return (
    <>
      <Router>
        <Header />
        <Routes>
          <Route path='/' element={<Home />} />
          <Route path='/about' element={<About />} />
          <Route path='/courses' element={<CourseHome />} />
          <Route path='/team' element={<Team />} />
          <Route path='/pricing' element={<Pricing />} />
          <Route path='/journal' element={<Blog />} />
          <Route path='/contact' element={<Contact />} />
        </Routes>

        <div>
          <div className="enroll">
            {/* Afficher le bouton toujours, mÃªme si enroll est vrai */}
            {!enroll && <button className="enroll-button" onClick={handleChange}>C'est parti!</button>}
          </div>

          {/* Condition ternaire pour afficher Chatbot ou Final */}
          {enroll && (page3 ? <Final /> : 
            <div className="App">
              <Chatbot
                config={config}
                messageParser={MessageParser}
                actionProvider={ActionProvider}
              />
            </div>
          )}
        </div>

        <Footer /> {/* Inclure le composant Footer ici */}
      </Router>
    </>
  );
}

export default App;
