import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Heading from "../../common/heading/Heading";
import "./Hero.css";

const Hero = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000 // Changez la vitesse du carrousel selon vos besoins
  };

  return (
    <>
      <section className='hero'>
        <Slider {...settings}>
          <div>
            <img src="../../../../public/images/bg.webp" alt="Background 1" />
          </div>
          <div>
            <img src="../../../../public/images/back.webp" alt="Background 2" />
          </div>
          <div>
            <img src="../../../../public/images/awra.webp" alt="Background 3" />
          </div>
        </Slider>
        <div className='container'>
          <div className='row'>
            <Heading subtitle='Bienvenue dans notre FacultÃ© des Sciences Juridiques, Economiques et Sociales de Marrakech' title='' />
            <div className='button'>
              <button className='primary-btn'>
                GET STARTED NOW <i className='fa fa-long-arrow-alt-right'></i>
              </button>
            </div>
          </div>
        </div>
      </section>
      <div className='margin'></div>
    </>
  );
}

export default Hero;
