import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Heading from "../../common/heading/Heading";
import "./Hero.css";

const Hero = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000 // Changez la vitesse du carrousel selon vos besoins
  };
  function handleClick() {
    const element = document.querySelector('.one');
    element.classList.add('clicked');
  }


  return (
    <>
      <section className='hero'>
        <Slider {...settings}>

          <div >
            <img src="./Capture2500.PNG" alt="Background 2" />
          </div>
          <div>
            <img src="./Capture.PNG" alt="Background 3" />
          </div>
          <div>
            <img src="./tsswira.png" alt="Background 3" />
          </div>
        </Slider>
        <div className='container'>
          <div className='row'>
            <Heading subtitle="Study in Ibn Zohr " title='Ibn zohr  University' />

            <div className='one'>
              <button onClick={handleClick}>
                GET STARTED NOW <i className='fa fa-long-arrow-alt-right'></i>
              </button>
            </div>
            <div class='cadre1'>

                     </div>
          </div>
        </div>
      </section>
      <div className='margin'></div>
    </>
  );
}

export default Hero;
