import React, { useState } from 'react';
import { useHistory } from 'react-router-dom'; // Importez useHistory depuis react-router-dom
import Heading from '../../common/heading/Heading';
import Chatbot from 'react-chatbot-kit';
import config from '../../../chatbot/config';
import MessageParser from '../../../chatbot/messageParsers';
import ActionProvider from '../../../chatbot/actionProvider';
import './Hero.css';

const Hero = () => {
  const history = useHistory(); // Initialisez useHistory

  const [enroll, setEnroll] = useState(false);

  function handleChange() {
    setEnroll(true);
    history.push('/chat'); // Redirigez vers votre chemin souhaité lors du changement
  }

  return (
    <>
      <section className='hero'>
        <div className='container'>
          <div className='row'>
            <Heading subtitle='Bienvenue dans notre Faculté des Sciences Juridiques, Economiques et Sociales de Marrakech' title='' />
            <div className='button'>
              {enroll ? (
                <Chatbot
                  config={config}
                  messageParser={MessageParser}
                  actionProvider={ActionProvider}
                />
              ) : (
                <button className='primary-btn' onClick={handleChange}>
                  GET STARTED NOW <i className='fa fa-long-arrow-alt-right'></i>
                </button>
              )}
            </div>
          </div>
        </div>
      </section>
      <div className='margin'></div>
    </>
  );
};

export default Hero;
